# Example python function for using known text, this shows writing "let us have a fun time" at the point (x,y)'
def generate_coordinateDict(textX, textY):
  coordinateDict = {
    "elementProperties": {
      "type": "text"
    }
    "startCoordinates": [textX, textY],
    "textResponse": "Let us have a fun time"
  }
  return coordinateDict
coordinateDict = generate_coordinateDict(textX, textY)

