import requests

def bing_web_search(search_term, subscription_key, search_url, source_node):
    headers = {"Ocp-Apim-Subscription-Key": subscription_key}
    params = {"q": search_term, "textDecorations": True, "textFormat": "HTML"}

    try:
        response = requests.get(search_url, headers=headers, params=params)
        response.raise_for_status()
        search_results = response.json()

        graph_thoughts = []
        if "webPages" in search_results and "value" in search_results["webPages"]:
            for i, result in enumerate(search_results["webPages"]["value"][:5], start=1):
                thought = f"Result {i}: Title: {result['name']}, URL: {result['url']}, Description: {result['snippet']}"
                graph_thoughts.append(thought)
        else:
            graph_thoughts.append("No results found or an error occurred.")

        result = {
            "sourceNode": source_node,
            "graphThoughts": graph_thoughts,
            "nextCapability": "EvaluateOptionsCapability"
        }

        return result
    except requests.exceptions.HTTPError as err:
        error_message = f"HTTP error occurred: {err}"
        result = {
            "sourceNode": source_node,
            "graphThoughts": [error_message],
            "nextCapability": "EvaluateOptionsCapability"
        }
        return result
    except Exception as err:
        error_message = f"An error occurred: {err}"
        result = {
            "sourceNode": source_node,
            "graphThoughts": [error_message],
            "nextCapability": "EvaluateOptionsCapability"
        }
        return result

